import React from 'react';
import './App.css';
import {useState} from 'react';

function App() {
  const [lista, setLista] = useState([]);

  const addLista = ()=> {
    let val = document.getElementById('inp').value;
    let datanow = new Date().getTime();
    setLista([...lista, [datanow,val]]);
    console.log(lista)
  }
  return (
   <div id="container">
     <div id="cont">
     <input id="inp"/>
     <button onClick={addLista}>Add new</button>
     </div>

     <div>
        {lista.map(e=>{
          return <div id={e[0]} key={e[0]}>{e[1]}</div>
        })}
     </div>
   </div>
  );
}

export default App;
