import React from 'react';
import './App.css';
import {useState} from 'react';

function App() {
  const [lista, setLista] = useState([]);

  const addLista = ()=> {
    let val = document.getElementById('inp').value;
    let datanow = new Date().getTime();
    setLista([...lista, [datanow,val, false]]);
    console.log(lista)
  }

  let msg = ()=> {
    if(!lista){    
      return <div key="asd">D</div>
    }
  }
  return (
    <div id="container">
      <h1 className='title'>To-do List</h1>
        <div id="cont">
          <input id="inp" placeholder='Adicione um novo item...'/>
          <button className="bt" onClick={addLista}>Add new</button>
        </div>

        <div id="cards-out">
            {[...lista].reverse().map(e=>{
              return <div className="card" key={e[0]}>{e[1]}</div>;    
            })}
            {msg()}
        </div>
    </div>
  );
}

export default App;
